import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export const UserHandlePage = () => {
  const [matchUserData, setMatchUserData] = useState(null);
  const [iconAsBinary, setIconAsBinary] = useState(null);
  const { username } = useParams();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(`/users/${username}`);
        const data = await response.json();
        if (response.ok) {
          setMatchUserData(data.data);
          if (data.data.iconAsBinary) {
            setIconAsBinary(data.data.iconAsBinary.buffer);
          }
        } else {
          console.log(data.message);
        }
      } catch (error) {
        console.log("Error:", error);
      }
    };

    fetchUserData();
  }, [username]);

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
      reader.readAsDataURL(file);
    });
  };

  if (!matchUserData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{matchUserData.username}</h1>
      <p>Email: {matchUserData.bio}</p>
      {iconAsBinary && (
        <img
          src={`data:image/png;base64,${Buffer.from(iconAsBinary).toString("base64")}`}
          alt="User Icon"
        />
      )}
      {/* Render other user data as needed */}
    </div>
  );
};
