import { useState } from "react";
import styled from "styled-components";
import Login from "../Login/Login";

export const CreateUserPage = () => {
  const userInitialState = {
    username: "",
    password: "",
    email: "",
    icon: "",
    bio: "",
    link: "",
  };
  const [userData, setUserData] = useState(userInitialState);
  const [fileName, setFileName] = useState();
  const [fileUploadSucceed, setFileUploadSucceed] = useState(true);
  const [showConfirmation, setShowConfirmation] = useState(false);

  const handleChange = (value, name) => {
    setUserData({ ...userData, [name]: value });
  };

  const handleFileChange = (event) => {
    setFileName(event.target.value);
    var files = event.target.files;
    const imageFile = files[0];

    readFileAsBytes(imageFile)
      .then((result) => {
        setFileUploadSucceed(true);
        setUserData({ ...userData, ["icon"]: result });
      })
      .catch((err) => {
        console.log("icon upload error:", err);
        setFileUploadSucceed(false);
      });
  };

  const readFileAsBytes = async (imageFile) => {
    return new Promise((resolve, reject) => {
      let reader = new FileReader();
      reader.onerror = reject;
      reader.onload = (e) => resolve(e.target.result);
      reader.readAsArrayBuffer(imageFile);
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isUrlValid(userData.link)) {
      // don't submit, link error
      console.log("link wrong dont submit");
    } else if (!fileUploadSucceed) {
      console.log("file wrong dont submit");
      // dont submit, something wrong with file
    } else {
      console.log(userData);
      // submit the data :)
      try {
        const requestOptions = {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(userData),
        };
        const response = await fetch("/newuser", requestOptions);
        const data = await response.json();



        console.log(data);
        setShowConfirmation(true);
      } catch (err) {
        console.log("Error");
      }
      console.log(userData.icon);


    }
  };

  const isUrlValid = async (string) => {
    if (string) {
      return true;
    } else {
      try {
        await new URL(string);
        return true;
      } catch (err) {
        return false;
      }
    }
  };

  return (
    <>
      <Login />
      <form onSubmit={handleSubmit}>
        <div>
          <label for="username">username:</label>
          <input
            id="username"
            type="text"
            value={userData.username}
            minLength="5"
            maxLength="20"
            onChange={(ev) => handleChange(ev.target.value, "username")}
          />
          <label for="password">password:</label>
          <input
            id="password"
            type="password"
            value={userData.password}
            minLength="5"
            maxLength="20"
            onChange={(ev) => handleChange(ev.target.value, "password")}
          />
          <label for="email">email:</label>
          <input
            id="email"
            type="email"
            value={userData.email}
            onChange={(ev) => handleChange(ev.target.value, "email")}
          />
          <label for="icon">icon:</label>
          <input
            id="icon"
            type="file"
            value={fileName}
            minLength="5"
            maxLength="20"
            accept="image/*"
            onChange={(ev) => handleFileChange(ev, "icon")}
          />
          <label for="bio (optional)">bio (optional):</label>
          <input
            id="bio"
            type="text"
            value={userData.bio}
            minLength="0"
            maxLength="1000"
            onChange={(ev) => handleChange(ev.target.value, "bio")}
          />
          <label for="link">website link (optional):</label>
          <input
            id="link"
            type="text"
            value={userData.link}
            minLength="0"
            maxLength="1000"
            onChange={(ev) => handleChange(ev.target.value, "link")}
          />

          <button submit>Submit</button>
        </div>
      </form>
      {showConfirmation && (
      <ConfirmationPopUp closeConfirmation={closeConfirmation}>
      <PopUpContent>
        <h2>User Created</h2>
        <p>User has been created successfully.</p>
        <CloseButton onClick={closeConfirmation}>Close</CloseButton>
      </PopUpContent>
    </ConfirmationPopUp>
      )}

    </>
  );
};

const ConfirmationPopUp = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const PopUpContent = styled.div`
  background-color: #fff;
  padding: 20px;
  border-radius: 5px;
  text-align: center;
`;

const CloseButton = styled.button`
  margin-top: 10px;
`;
