import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";

export const UserHandlePage = () => {
  const [matchUserData, setMatchUserData] = useState(null);
  const [profilePic, setProfilePic] = useState("")
  const { username } = useParams();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(`/users/${username}`);
        const data = await response.json();
        if (response.ok) {
          setMatchUserData(data.data);
          const iconBase64 = matchUserData.icon.toString('base64');
          const iconSrc = `data:image/png;base64,${iconBase64}`;
          setProfilePic(iconSrc)

        } else {
          console.log(data.message);
        }
      } catch (error) {
        console.log("Error:", error);
      }
    };

    fetchUserData();
  }, [username]);





  if (!matchUserData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{matchUserData.username}</h1>
      <p>Email: {matchUserData.email}</p>
      <bio>Bio: {matchUserData.bio}</bio>
      {profilePic} : <Image src={iconSrc} alt="User Icon" />
      {/* Render other user data as needed */}
    </div>
  );
};

const Image = styled.img`
  width: 100px;
  height: auto;
`
