      <AppPage>
        <Sidebar />
        <Error />
      </AppPage>
    );
  } else {
    return (
      <AppPage>
        <Sidebar />
        <Routes>
          {/* <Route path="/" element={<HomeFeed />} /> */}
          <Route path="/upload" element={<PostUploadPage />} />
        </Routes>
      </AppPage>
    );
  }
};

const AppPage = styled.div`
  margin: 20px;
  display: flex;
  flex-direction: row;
  gap: 10px;
`;


export default App;
