import {useState, useCallback} from 'react';
import styled from 'styled-components';
var Binary = mongo.Binary;

export const CreateUserPage = () => {
  const userInitialState = {
    username: "",
    password: "",
    email: "",
    icon: "",
    bio: "",
    link: ""
  }
  const [userData, setUserData] = useState(userInitialState);

  const handleChange = (value, name) => {
      setUserData({ ...userData, [name]: value });
    };

    const handleFileChange = (event, name) => {
      const files = event.target.files;
      const imageFile = files[0];

      console.log(imageFile);
    }


  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isUrlValid(userData.link)) {
      // don't submit, link error
      console.log("link wrong dont submit")
    } else {
      // submit the data :)
      console.log(userData.icon);

    }
  };



  const isUrlValid = async (string) => {
    if (string) {
      return true;
    } else {
      try {
        await new URL(string);
        return true;
      } catch (err) {
        return false;
      }
    }

  }

  return (
      <form onSubmit={handleSubmit}>
        <div>
          <label for="username">username:</label>
          <input 
          id="username"
          type="text"
          value={userData.username}
          minLength="5"
          maxLength="20"
          onChange={(ev) => handleChange(ev.target.value, "username")}
           />
          <label for="password">password:</label>
           <input 
          id="password"
          type="text"
          value={userData.password}
          minLength="5"
          maxLength="20"
          onChange={(ev) => handleChange(ev.target.value, "password")}
           />
          <label for="email">email:</label>
          <input 
          id="email"
          type="email"
          value={userData.email}
          onChange={(ev) => handleChange(ev.target.value, "email")}

           />
           <label for="icon">icon:</label>
           <input 
          id="icon"
          type="file"
          value={userData.icon}
          minLength="5"
          maxLength="20"
          onChange={(ev) => handleFileChange(ev, "icon")}
           />
           <label for="bio (optional)">bio (optional):</label>
          <input 
          id="bio"
          type="text"
          value={userData.bio}
          minLength="0"
          maxLength="1000"
          onChange={(ev) => handleChange(ev.target.value, "bio")}
           />
           <label for="link">website link (optional):</label>
           <input 
          id="link"
          type="text"
          value={userData.link}
          minLength="0"
          maxLength="1000"
          onChange={(ev) => handleChange(ev.target.value, "link")}
           />


          <button submit>Submit</button>
        </div>
      </form>
  );
}
