import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";

export const UserHandlePage = () => {
  const [matchUserData, setMatchUserData] = useState(null);
  const [iconBinary, setIconBinary] = useState(null);
  // const [isHovered, setIsHovered] = useState(false);
  const { username } = useParams();


  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(`/users/${username}`);
        const data = await response.json();
        if (response.ok) {
          setMatchUserData(data.data);
          if (data.data.iconBinary) {
            setIconBinary(data.data.iconBinary.buffer);
          }
        } else {
          console.log(data.message);
        }
      } catch (error) {
        console.log("Error:", error);
      }
    };

    fetchUserData();
  }, [username]);

  // const handleMouseEnter = () => {
  //   setIsHovered(true);
  // };

  // const handleMouseLeave = () => {
  //   setIsHovered(false);
  // };


  const iconBase64 = matchUserData.icon.toString('base64');
  const iconSrc = `data:image/png;base64,${iconBase64}`;

  if (!matchUserData) {
    return <div>Loading...</div>;
  }

  return (
    <Container >
      <h1>{matchUserData.username}</h1>
      <p>Email: {matchUserData.email}</p>
      <p>Bio: {matchUserData.bio}</p>
      {isHovered ? (
        <img src={iconSrc} alt="Hover User Icon" />
      ) : (<p>No image</p>
      )}
      {/* Render other user data as needed */}
    </Container>
  );
};

const Container = styled.div`
  max-width: 500px;
`
