import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export const UserHandlePage = () => {
  const [matchUserData, setMatchUserData] = useState(null);
  const { username } = useParams();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(`/user/${username}`);
        const data = await response.json();
        if (response.ok) {
          const iconAsBase64 = data.data.iconAsBinary ? await convertToBase64(data.data.iconAsBinary) : null;
          setMatchUserData({ ...data.data, icon: iconAsBase64 });
        } else {
          console.log(data.message);
        }
      } catch (error) {
        console.log("Error:", error);
      }
    };

    fetchUserData();
  }, [username]);

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
      reader.readAsDataURL(file);
    });
  };
  console.log(convertToBase64)
  if (!matchUserData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{matchUserData.username}</h1>
      <p>Email: {matchUserData.bio}</p>
      {matchUserData.icon && <img src={matchUserData.icon} alt="User Icon" />}
      {/* Render other user data as needed */}
    </div>
  );
};
