import {useState, useCallback} from 'react';
import styled from 'styled-components';

export const CreateUserPage = () => {
  const userInitialState = {
    username: "",
    password: "",
    email: "",
    icon: "",
    bio: "",
    link: ""
  }
  const [userData, setUserData] = useState(userInitialState);
  const [linkValid, setLinkValid] = useState(true);

  const handleChange = (value, name) => {
      setUserData({ ...userData, [name]: value });
    };

  const handleLinkChange = (value, name) => {
    if (isUrlValid(value)) {
      setUserData({ ...userData, [name]: value });
    } else {
      setLinkValid(false);
    }
  };


  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!linkValid) {
      // don't submit, have an error message
    } else {
      // submit the data :)
    }
    console.log(userData);
  };



  function isUrlValid(string) {
    try {
      new URL(string);
      return true;
    } catch (err) {
      return false;
    }
  }

  return (
      <form onSubmit={handleSubmit}>
        <div>
          <label for="username">username:</label>
          <input 
          id="username"
          type="text"
          value={userData.username}
          minLength="5"
          maxLength="20"
          onChange={(ev) => handleChange(ev.target.value, "username")}
          required />
          <label for="password">password:</label>
           <input 
          id="password"
          type="text"
          value={userData.password}
          minLength="5"
          maxLength="20"
          onChange={(ev) => handleChange(ev.target.value, "password")}
          required />
          <label for="email">email:</label>
          <input 
          id="email"
          type="email"
          value={userData.email}
          onChange={(ev) => handleChange(ev.target.value, "email")}
          required
           />
           <label for="bio (optional)">bio (optional):</label>
          <input 
          id="bio"
          type="text"
          value={userData.bio}
          minLength="0"
          maxLength="1000"
          onChange={(ev) => handleChange(ev.target.value, "bio")}
           />
           <label for="link">website link (optional):</label>
           <input 
          id="link"
          type="text"
          value={userData.link}
          minLength="0"
          maxLength="1000"
          onChange={(ev) => handleLinkChange(ev.target.value, "link")}
           />


          <button submit>Submit</button>
        </div>
      </form>
  );
}
