import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled, { keyframes } from "styled-components";

export const PostDetailsPage = () => {
const [matchPostData, setMatchPostData] = useState([]);
const [postPic, setPostPic] = useState("");
const { postId } = useParams();

  useEffect(() => {
    const fetchPostDetails = async () => {
      try {
        const response = await fetch(`/posts/${postId}`);
        const data = await response.json();

        if (response.ok) {
            setMatchPostData(data.data);
            // const fitPicBase64 = data.data.fitPic.toString("base64");
            // const fitPicSrc = `data:image/png;base64,${fitPicBase64}`;
            // setPostPic(fitPicSrc);
        } else {
          console.log(data.message);
        }
      } catch (error) {
        console.error("Error:", error);
      }
    };

    fetchPostDetails();
  }, [postId]);

  if (!matchPostData) {
    return <div>Loading...</div>;
  }

  // Render post details
  return (
    <div>
      <h1>Post Details</h1>
      <p>{matchPostData.title}</p>
      <p>{matchPostData.description}</p>
      {/* Render other post details */}
    </div>
  );
};
