import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled, { keyframes } from "styled-components";

export const PostDetailsPage = () => {
const [matchPostData, setMatchPostData] = useState([]);
const [postPic, setPostPic] = useState("");

const { postId } = useParams();

  useEffect(() => {
    const fetchPostDetails = async () => {
      try {
        const response = await fetch(`/posts/${postId}`);
        const data = await response.json();

        if (response.ok) {
            setMatchPostData(data.data[0]);
            const fitPicBase64 = data.data[0].fitPic.toString("base64");
            const fitPicSrc = `data:image/png;base64,${fitPicBase64}`;
            setPostPic(fitPicSrc);
        } else {
          console.log(data.message);
        }
      } catch (error) {
        console.error("Error:", error);
      }
    };

    fetchPostDetails();
  }, [postId]);

console.log(matchPostData)
const downloadPDF = (pdf) => {
    const linkSource = `data:application/pdf;base64,${pdf}`;
    const downloadLink = document.createElement("a");
    const fileName = "vct_illustration.pdf";

    downloadLink.href = linkSource;
    downloadLink.download = fileName;
    downloadLink.click();
  };

  if (!matchPostData) {
    return <div>Loading...</div>;
  }

  // Render post details
  return (
    <Window>
      <H1>MAKE IT YOURSELF</H1>
      {/* Render other post details */}
      <Container>
      <Image src={postPic} alt="User Icon" />
      <p>{matchPostData.title}</p>
      <p>{matchPostData.description}</p>
      <p>{matchPostData.sourceLink}</p>
      </Container>
    </Window>
  );
};

const Image = styled.img`
  position: fixed;
  bottom: 0px;
  left: 20px;
  right: 50px;
  z-index: 1;
  width: auto;
  height: 80vh;
`;

const Window = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: flex-end;
  height: 100vh;
  width: 100vw;
  padding: 20px;
  overflow: auto;
`;

const Container =styled.div`
  background-color: transparent;
  margin-bottom: 60px;
  display: flex;
  flex-direction: column;
  gap: 20px;
`

const H1 =styled.h1`
    z-index: 9999;
`
