import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export const UserHandlePage = () => {
  const [matchUserData, setMatchUserData] = useState(null);
  const [iconBinary, setIconBinary] = useState(null);
  const { username } = useParams();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(`/users/${username}`);
        const data = await response.json();
        if (response.ok) {
          setMatchUserData(data.data);
          if (data.data.iconBinary) {
            setIconBinary(data.data.iconBinary.buffer);
          }
        } else {
          console.log(data.message);
        }
      } catch (error) {
        console.log("Error:", error);
      }
    };

    fetchUserData();
  }, [username]);



