import {useState, useCallback} from 'react';
import styled from 'styled-components';

export const CreateUserPage = () => {

  const userInitialState = {
    username: "",
    password: "",
    email: "",
    icon: "",
    bio: "",
    link: ""
  }
  const [userData, setUserData] = useState(userInitialState);

  const handleChange = (value, name) => {
      setUserData({ ...userData, [name]: value });
    };

    const handleFileChange = (event, name) => {
      const files = event.target.files;
      const imageFile = files[0];
      console.log(event.target.value)

      // Pass the file to the blob, not the input[0].
    fileData = new Blob([files[0]]);
    // Pass getBuffer to promise.
    var promise = new Promise(getBuffer);
    // Wait for promise to be resolved, or log error.
    promise.then(function(data) {
      // Here you can pass the bytes to another function.

      console.log(data.toString());
    }).catch(function(err) {
      console.log('Error: ', err);
    });

      setUserData({ ...userData, [name]: event.target.value });


    }
      /* 
    Create a function which will be passed to the promise
    and resolve it when FileReader has finished loading the file.
  */
  function getBuffer(resolve) {
    var reader = new FileReader();
    reader.readAsArrayBuffer(fileData);
    reader.onload = function() {
      var arrayBuffer = reader.result
      var bytes = new Uint8Array(arrayBuffer);
      resolve(bytes);
    }
  }


  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isUrlValid(userData.link)) {
      // don't submit, link error
      console.log("link wrong dont submit")
    } else {
      // submit the data :)
      try {
        const requestOptions = {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(userData)
      };
          // await fetch('/api/tweet', requestOptions);
          const response = await fetch("/user/newuser", requestOptions)
          const data = await response.json()

        console.log(data);
      } catch (err) {
        console.log("Error")
      }
      console.log(userData.icon);

    }
  };



  const isUrlValid = async (string) => {
    if (string) {
      return true;
    } else {
      try {
        await new URL(string);
        return true;
      } catch (err) {
        return false;
      }
    }

  }

  return (
      <form onSubmit={handleSubmit}>
        <div>
          <label for="username">username:</label>
          <input 
          id="username"
          type="text"
          value={userData.username}
          minLength="5"
          maxLength="20"
          onChange={(ev) => handleChange(ev.target.value, "username")}
           />
          <label for="password">password:</label>
           <input 
          id="password"
          type="text"
          value={userData.password}
          minLength="5"
          maxLength="20"
          onChange={(ev) => handleChange(ev.target.value, "password")}
           />
          <label for="email">email:</label>
          <input 
          id="email"
          type="email"
          value={userData.email}
          onChange={(ev) => handleChange(ev.target.value, "email")}

           />
           <label for="icon">icon:</label>
           <input 
          id="icon"
          type="file"
          value={userData.icon}
          minLength="5"
          maxLength="20"
          onChange={(ev) => handleFileChange(ev, "icon")}
           />
           <label for="bio (optional)">bio (optional):</label>
          <input 
          id="bio"
          type="text"
          value={userData.bio}
          minLength="0"
          maxLength="1000"
          onChange={(ev) => handleChange(ev.target.value, "bio")}
           />
           <label for="link">website link (optional):</label>
           <input 
          id="link"
          type="text"
          value={userData.link}
          minLength="0"
          maxLength="1000"
          onChange={(ev) => handleChange(ev.target.value, "link")}
           />


          <button submit>Submit</button>
        </div>
      </form>
  );
}
