import React, { useEffect, useState } from "react";
import styled from "styled-components";

export const IndexPage = () => {
  const [fitPic, setFitPic] = useState("");
  const [isHovered, setIsHovered] = useState(false);
  const [textHovered, setTextHovered] = useState(false);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
        try {
          const response = await fetch("/index"); // Make a GET request to the server endpoint
          const data = await response.json();
          if (response.ok) {
            setPosts(data.data);
            const fitPicBase64 = data.data.fitPic.toString("base64");
            const fitPicSrc = `data:image/png;base64,${fitPicBase64}`;
            setFitPic(fitPicSrc);
          } else {
            console.log(data.message); 
          }
        } catch (error) {
          console.error("Error:", error);
        }
      };

    fetchPosts();
  }, []);

  return (
    <div>
      <h1>Post Index</h1>
      {posts.map((post) => (
        <div key={post._id}>
          <h2>{post.title}</h2>
          <p>Username: {post.username}</p>
          <p>Description: {post.description}</p>
          {/* Render other post details */}
          {isHovered ? (
            <PopUpPic>
            {profilePic ? <Image src={fitPic} alt="User Icon" /> : null}
            </PopUpPic>) : null}
        </div>
      ))}
    </div>
  );
};
