import React from "react";
import { Route, Routes } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import { FiLoader } from "react-icons/fi";
import {PostUploadPage} from './components/PostUploadPage'

const App = (children) => {
  const { currentUser, status, hasError } = useCurrentUser();

  if (status === "loading" ) {
    return (
      <p>LOADING</p>
    );
  } else if ( hasError === true ) {
    return (
      <p>ERROR</p>
    );
  } else {
    return (
      <AppPage>
        <Routes>
          {/* <Route path="/" element={<HomeFeed />} /> */}
          <Route path="/upload" element={<PostUploadPage />} />
        </Routes>
      </AppPage>
    );
  }
};

const AppPage = styled.div`
  margin: 20px;
  display: flex;
  flex-direction: row;
  gap: 10px;
`;


export default App;
