import React, { useEffect, useState } from "react";
import styled, {keyframes} from "styled-components";

export const IndexPage = () => {
    const [isHovered, setIsHovered] = useState(false);
    const [textHovered, setTextHovered] = useState(false);
    const [posts, setPosts] = useState([]);

    useEffect(() => {
      const fetchPosts = async () => {
        try {
          const response = await fetch("/index"); // Make a GET request to the server endpoint
          const data = await response.json();
          if (response.ok) {
            const updatedPosts = await data.data.map((post) => {
                const fitPicBase64 = post.fitPic.toString("base64");
                const fitPicSrc = `data:image/png;base64,${fitPicBase64}`;
                return { ...post, fitPic: fitPicSrc };
              });
              setPosts(updatedPosts);
          } else {
            console.log(data.message);
          }
        } catch (error) {
          console.error("Error:", error);
        }
      };

      fetchPosts();
    }, []);

    const handleMouseEnter = () => {
      setIsHovered(true);
    };

    const handleMouseLeave = () => {
      setIsHovered(false);
    };

    const textMouseEnter = () => {
      setTextHovered(true);
    };

    const textMouseLeave = () => {
      setTextHovered(false);
    };

    if (!posts) {
        return <div>Loading...</div>;
      }

      return (
        <Window>
          <Container>
            <h1>Post Index</h1>
            {posts.map((post) => (
              <Card key={post._id}
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}>
                <Text
                  onMouseEnter={textMouseEnter}
                  onMouseLeave={textMouseLeave}
                  isHovered={textHovered}
                >
                  {post.title}
                </Text>
                <p>Title: {post.title}</p>
                <p>Description: {post.description}</p>
                <p>Username: {post.username}</p>

              </Card>
            ))}
            {posts.map((post) => (
              <Pic key={post._id}>
                {isHovered && (
                    <Image src={post.fitPic} alt="Fit Post" />
                )}
              </Pic>
            ))}
          </Container>
        </Window>
      );
}

  const Window = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  height: 100vh;
  width: 100vw;
  padding: 20px;
`

const Image = styled.img`
  width: auto;
  height: 40vh;
`;

const Container =styled.div`
  background-color: transparent;
`

const animateLetterSpacing = keyframes`
  from {
    letter-spacing: normal;
  }
  to {
    /* font-family: Helvetica; */
    letter-spacing: 20px;
  }
`;

const Text = styled.h1`
  letter-spacing: normal;
  font-size: 30px;
  transition: letter-spacing 0.1 ease;
  &:hover {
    animation: ${animateLetterSpacing} 0.5 forwards;
  }
`;

const Card = styled.div`
  background-color: transparent;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  width: 70vw;

`;

const Pic = styled.div`
  position: fixed;
  top: 0px;
  right: 50px;
  z-index: 9999;
`;
