import React, { useEffect, useState } from "react";

export const PostUploadPage = () => {
  const [formData, setFormData] = useState({
    title: "",
    username: "",
    description: "",
    patternFile: "",
    theFit: "",
    garmentType:""  
  });
  const [disabled, setDisabled] = useState(true);
  const [errMessage, setErrMessage] = useState("");


  useEffect(() => {
    Object.values(formData).includes("") || formData.order === "undefined"
      ? setDisabled(true)
      : setDisabled(false);
  }, [formData, setDisabled]);


  const handleChange = (value, name) => {
    setFormData({ ...formData, [name]: value });
    setErrMessage("");
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    try {
      const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
    };
        // await fetch('/api/tweet', requestOptions);
        const response = await fetch("/upload", requestOptions)
        const data = await response.json()

      console.log(data);
    } catch (err) {
      console.log("Error")
    }


  }


  return (
    <div>
      <form onSubmit={onSubmit}>
    <input type="text" id="postTitle" name="postTitle" required
     minlength="10" maxlength="100" size="10" value={formData.title} onChange={(ev) => handleChange(ev.target.value, "title")}/>
    {/* <input type="text" id="postDescription" name="postDescription" 
     minlength="0" maxlength="1000" size="10" />
    <input type="file"
     id="fitImage" name="fitImage"
     accept="image/png, image/jpeg" />
    <input type="file"
     id="patternPdf" name="patternPdf"
     accept="application/pdf" /> */}
     <input type="submit" value="Upload post"/>
</form>
  </div>
  )
};
