import React, { useEffect, useState } from "react";
import styled, {keyframes} from "styled-components";

export const IndexPage = () => {
    const [isHovered, setIsHovered] = useState(false);
    const [textHovered, setTextHovered] = useState(false);
    const [posts, setPosts] = useState([]);

    useEffect(() => {
      const fetchPosts = async () => {
        try {
          const response = await fetch("/index"); // Make a GET request to the server endpoint
          const data = await response.json();
          if (response.ok) {
            const updatedPosts = data.data.map((post) => {
                const fitPicBase64 = post.fitPic.toString("base64");
                const fitPicSrc = `data:image/png;base64,${fitPicBase64}`;
                return { ...post, fitPic: fitPicSrc };
              });
              setPosts(updatedPosts);
          } else {
            console.log(data.message);
          }
        } catch (error) {
          console.error("Error:", error);
        }
      };

      fetchPosts();
    }, []);

    const handleMouseEnter = () => {
      setIsHovered(true);
    };

    const handleMouseLeave = () => {
      setIsHovered(false);
    };

    const textMouseEnter = () => {
      setTextHovered(true);
    };

    const textMouseLeave = () => {
      setTextHovered(false);
    };

    return (
      <Window>
        <Container
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          <h1>Post Index</h1>
          {posts.map((post) => (
            <div key={post._id}>
              <Text
                onMouseEnter={textMouseEnter}
                onMouseLeave={textMouseLeave}
                isHovered={textHovered}
              >
                {post.title}
              </Text>
              <p>Username: {post.username}</p>
              <p>Description: {post.description}</p>
              {isHovered && (
                <PopUpPic>
                  <Image src={post.description.fitPic} alt="User Icon" />
                </PopUpPic>
              )}
            </div>
          ))}
        </Container>
      </Window>
    );
  };

  const Window = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  height: 100vh;
  width: 100vw;
  padding: 20px;
`

const Image = styled.img`
  width: auto;
  height: 80vh;
`;

const Container =styled.div`
  background-color: transparent;
`
const PopUpPic =styled.div`
  padding: 30px;
  margin: 20px;
`

const animateLetterSpacing = keyframes`
  from {
    letter-spacing: normal;

  }
  to {
    /* font-family: Helvetica; */
    letter-spacing: 50px;
  }
`;

const Text = styled.h1`
  letter-spacing: normal;
  transition: letter-spacing 0.1 ease;
  &:hover {
    animation: ${animateLetterSpacing} 0.5 forwards;
  }
`;
