import React, { useEffect, useState } from "react";

export const PostUploadPage = () => {
  const [formData, setFormData] = useState({});
  const [disabled, setDisabled] = useState(true);
  const [errMessage, setErrMessage] = useState("");


  useEffect(() => {
    Object.values(formData).includes("") || formData.order === "undefined"
      ? setDisabled(true)
      : setDisabled(false);
  }, [formData, setDisabled]);


  const handleChange = (value, name) => {
    setFormData({ ...formData, [name]: value });
    setErrMessage("");
  };

  const onSubmit = () => {
    console.log("submit form")
  }


  return (
    <div>
      <form onSubmit={onSubmit}>
    <input type="text" id="postTitle" name="postTitle" required
     minlength="10" maxlength="100" size="10" />
    <input type="text" id="postDescription" name="postDescription" 
     minlength="0" maxlength="1000" size="10" />
    <input type="file"
     id="fitImage" name="fitImage"
     accept="image/png, image/jpeg" />
    <input type="file"
     id="patternPdf" name="patternPdf"
     accept="application/pdf" />
</form>
  </div>
  )
};
